(function($){$.fn.toolTip=function(options){var defaults={background:'#1e2227',color:'#fff',opacity:'0.8'},options=$.extend(defaults,options);return this.each(function(){var elem=$(this);var title=elem.attr('title');if(title!=''){var tooltip=$('<div id="tooltip" />');elem.attr('title','');elem.hover(function(e){tooltip.hide().appendTo('body').html(title).hide().css({'background-color':options.background,'color':options.color,'opacity':options.opacity,}).fadeIn(500);},function(){tooltip.remove();});}
elem.mousemove(function(e){tooltip.css({top:e.pageY+10,left:e.pageX+20});});});}})(jQuery);