<template>
  <div class="height100" style="margin: 0 20px;">
    <el-table
          :data="tableData"
          width="100%"
          height="100%"
          border
          v-loadmore="{noMoreVar:'tlm_noMore', loadingVar: 'loading', value: load}"
          v-loading="loading"
          :element-loading-text="$t('retreat.loadInfo')"
          highlight-current-row
          @row-click="rowClick"
        >
          <el-table-column
            v-for="(item, index) in tableParams"
            :key="index"
            :prop="item.prop"
            :label="item.label"
            :min-width="item.width"
            :align="item.align || 'left'"
            :header-align="item.headerAlign || 'left'"
            :fixed="item.fixed"
          >
            <template slot-scope="scope">
              <template v-if="item.prop == 'patientGender'">
                <span
                  :val="scope.row[item.prop]"
                  code="GENDER_CODE"
                  v-codeTransform
                  v-if="scope.row[item.prop]"
                ></span>
              </template>
              <template v-else>
                <span class="overflow-point">{{ scope.row[item.prop] }}</span>
              </template>
            </template>
          </el-table-column>
        </el-table>
  </div>
</template>

<script>
import service from "@/api/appointment/retreat";
import { mapActions, mapGetters } from "vuex";
import appointmentService from "@/api/appointment/appointment";

export default {
  name: "patientInfo",
   data() {
    return {
      param: {},
      searchValue: "",
      // 患者列表表格
      tableParams: [
        {
          label: "患者姓名",
          prop: "patientName",
          width: "100",
          align: "left",
          fixed: false
        },
        {
          label: "年龄",
          prop: "patientAge",
          width: "100",
          align: "left",
          fixed: false
        },
        {
          label: "性别",
          prop: "patientGender",
          width: "100",
          align: "left",
          fixed: false
        }
      ],
      tableData: [],
      // 分页数据
      pageParams: {
        pageNo: 1,
        pageSize: 15,
        total: 0
      },
      // loading
      loading: false
    };
  },
  computed: {
    ...mapGetters("base", ["patientListData"]),
    tlm_noMore() {
      return (
        Math.ceil(this.pageParams.total / this.pageParams.pageSize) <=
        this.pageParams.pageNo
      );
    }
  },
  watch: {},
  mounted() {
    this.search({});
  },
  methods: {
    ...mapActions({
      changeRecPatientData: "base/changeRecPatientData",
      changePatientListData: "base/changePatientListData"
    }),
    //左侧列表转换tab
    handleClick() {},
    // 查询方法
    search(form) {
      this.searchValue = form.search || "";
      this.pageParams.pageNo = 1;
      this.getPatientList();
    },
    // 读卡方法
    async readCard(form) {
      try {
        this.$showLoading();
        let res = await swipe({ cardNum: form.search || "" });
        // let res = await appointment.getCardDetailInfo({
        //   cardNum: form.searchValue || ""
        // });
        if (!res.data.patientId) {
          this.$message("未查询到患者");
        }else{
          this.$set(res.data,"headImg",require('@/assets/pojehjdz.jpeg'))
          this.$set(res.data,"qrcodeImg",'data:image/png;base64,')
        }
        this.tableData = [res.data];
        this.$hideLoading();
      } catch (error) {
        this.$hideLoading();
        console.info(error);
        this.$message.error("读卡失败");
      }
    },
    // 获取患者列表
    async getPatientList() {
      try {
        this.loading = true;
        let param = {
          ...this.pageParams,
          search: this.searchValue,
          reservation: 0
        };
        let result = await getArchListNew(param);
        if(result.data){
          result.data.forEach(val =>{
            this.$set(val,"headImg",require('@/assets/pojehjdz.jpeg'))
            this.$set(val,"qrcodeImg",'data:image/png;base64,')
          })
        }
        if (this.pageParams.pageNo === 1) {
          this.tableData = result.data;
        } else {
          this.tableData = result.data.concat(this.tableData);
        }
        this.pageParams.total = result.total;
        this.loading = false;
      } catch (error) {
        this.loading = false;
        console.info(error);
        this.$message.error(error.msg || "获取患者列表失败");
      }
    },
    // load
    load() {
      if (this.tlm_noMore) {
        return;
      }
      this.pageParams.pageNo++;
      this.getPatientList();
    },
    rowClick(row) {
      this.changeRecPatientData(row);
      this.changePatientListData(row);
    },

    //工具箱
    messageHandler() {}
  }
};
</script>

