<template>
  <div class="leftList height100">
    <div class="table-cont height100">
      <el-table
        :data="patientList"
        border
        width="100%"
        height="100%"
        v-loadmore="load"
        v-loading="tlm_isLoading"
        element-loading-text="加载中..."
        highlight-current-row
        @row-click="rowClick"
        ref="multipleTable"
        :row-class-name="tableRowClassName"
      >
        <!--<el-table-column
          v-for="(item,index) in patientListParams"
          :key="index"
          :prop="item.prop"
          :label="item.label"
          :min-width="item.width"
          :align="item.align || 'left'"
          :fixed="item.fixed"
          show-overflow-tooltip
        >
          <template slot-scope="scope">
           &lt;!&ndash; <div v-if="item.prop === 'identificationType'">
              <span :val="scope.row[item.prop]" code="MARK_TYPE_CODE" v-codeTransform></span>
            </div>&ndash;&gt;
            <div v-if="item.prop === 'archCreateState'">
              <span :val="scope.row[item.prop]" code="MARK_TYPE_CODE" v-codeTransform></span>
            </div>
            <div v-else>
              <span>{{scope.row[item.prop]}}</span>
            </div>
          </template>
        </el-table-column>-->
        <el-table-column
          prop="patientName"
          label="患者姓名"
          min-width="85"
          align="left"
          fixed="left"
          show-overflow-tooltip
        ></el-table-column>
        <el-table-column
          prop="cNum"
          label="卡号"
          min-width="100"
          align="left"
          show-overflow-tooltip
        >
          <template slot-scope="scope">
            <span>{{scope.row['cNum'] || '--'}}</span>
          </template>
        </el-table-column>
        <el-table-column
          prop="archCreateState"
          label="档案状态"
          min-width="110"
          align="left"
          show-overflow-tooltip
          sortable
        >
          <template slot-scope="scope">
            <span :val="scope.row['archCreateState']" code="ARCH_STATUS" v-codeTransform></span>
            <!-- <template v-if="scope.row['archCreateState'] == 0">未建档</template>
             <template v-else-if="scope.row['archCreateState'] == 1">未实名</template>
             <template v-else-if="scope.row['archCreateState'] == 2">实名</template>-->
          </template>
        </el-table-column>
      </el-table>
    </div>
  </div>
</template>

<script>
  import {getArchListNew, getArchInfoByCardNum, swipe} from '@/api/arch/arch.js';
  import {mapActions, mapGetters} from 'vuex';

  export default {
    name: "leftList",
    props: ['param', 'searchType'],
    data() {
      return {
        //分页信息
        pageNo: 1,
        pageSize: 10,
        total: 0,
        loading: false,
        patientListParams: [
          {
            prop: "patientName",
            label: "患者姓名",
            align: "left",
            width: "85",
            fixed: "left",
            sortable: false
          }, {
            prop: "patientId",
            label: "卡号",
            align: "left",
            width: "150",
            fixed: "left",
            sortable: false
          }, {
            prop: "archCreateState",
            label: "档案状态",
            align: "left",
            width: "110",
            fixed: "left",
            sortable: true
          }
          /*{
            prop: "identificationType",
            label: "介质类型",
            align: "left",
            width: "110",
            fixed: false
          }, {
            prop: "createdDate",
            label: "建档时间",
            align: "left",
            width: "150",
            fixed: false
          }*/
        ],
        patientList: [],
        patientListUnit: [],
        tlm_noMore: false,
        tlm_isLoading: false
      }
    },
    computed: {
      totalPage() {
        // debugger
        return Math.ceil(this.total / this.pageSize);
      },
      ...mapGetters("patientMaster", ["patientListData", "receivePatientData"])
    },
    watch: {
      'param': {
        async handler(n) {
          if (this.searchType == 'searchButton') {
            //查询按钮搜索
            let param = {
              ...this.param,
              cardNum: '',
              pageNo: 1,
              pageSize: this.pageSize
            };
            this.$delete(param,'date');
            await this.getPatientList(param);
            this.patientList = this.patientListUnit;
            // this.$delete(leftTabsValue,date);
            let leftTabsValue = {
              ...n,
              timeRange: [n["startTimeRange"] || '', n["endTimeRange"] || '']
            };
            if (this.patientList.length > 0) {
              await this.changeRecPatientData(this.patientList[0]);
              await this.changePatientListData(
                {
                  patientList: [...this.patientList],
                  pageNo: 1,
                  total: this.total,
                  leftTabsValue: leftTabsValue,
                  searchType: this.searchType,
                  clear: this.patientList[0].patientId ? '1' : '2'
                }
              );
              this.$refs.multipleTable.setCurrentRow(this.patientList[0]);
            } else {
              await this.changeRecPatientData({});
              await this.changePatientListData(
                {
                  patientList: [],
                  pageNo: 1,
                  total: 0,
                  leftTabsValue: leftTabsValue,
                  searchType: this.searchType,
                  clear: '1'
                }
              );
            }
          } else if (this.searchType == 'readCard') {
            //读卡
            await this.readCard();
            this.$refs.multipleTable.setCurrentRow(this.patientList[0]);
          }
        },
        deep: true,
        immediate: true
      },
      'patientListData': {
        async handler(n) {
          // debugger
          this.patientList = [];
          this.pageNo = 1;
          this.total = 1;
          if (typeof n.patientList !== 'undefined') {
            this.patientList = n.patientList;
            this.pageNo = n.pageNo || 1;
            this.total = n.total || 1;
            this.params = {...n.leftTabsValue} || {}
          }
        },
        deep: true,
        immediate: true
      }
    },
    async mounted() {
      if (this.patientList.length == 1) {
        this.$refs.multipleTable.setCurrentRow(this.patientList[0]);
      } else {
        this.$refs.multipleTable.setCurrentRow(this.receivePatientData);
      }
    },
    methods: {
      ...mapActions({
        changeRecPatientData: 'patientMaster/changeRecPatientData',
        changePatientListData: 'patientMaster/changePatientListData'
      }),
      search(){

      },
      //获取病人列表
      async getPatientList(param) {
        this.tlm_isLoading = true;
        let _self = this;
        try {

          //获取数据接口
          let result = await getArchListNew(param);
          // console.log(result)
          if (result.code == '1') {
            this.patientListUnit = [];
            if (result.data.length > 0) {
              result.data.forEach(function (item) {

                _self.patientListUnit.push({
                  patientId: item.patientId,
                  patientName: item.patientName,
                  patientGender: item.patientGender,
                  patientAge: item.patientAge,
                  mobileNum: item.mobileNum,
                  birthDate: item.birthDate,
                  identificationType: item.identificationType,
                  cardId: item.cardData,
                  createdDate: item.archCreateDate,
                  visitCode: item.visitCode,
                  ouptDeptId: item.ouptDeptId,
                  patientCode: item.patientCode,
                  headImg: require('@/assets/pojehjdz.jpeg'),
                  qrcodeImg: 'data:image/png;base64,',
                  identificationNum: item.identificationNum,
                  insuranceType: item.insuranceType,
                  chronicStatus: item.chronicStatus,
                  archCreateState: item.archCreateState,
                  relationStatus: item.relationStatus,
                  nativePlaceCode: item.nativePlaceCode,
                  cNum: item.cNum,//卡号
                  ssType: item.ssType,//社保
                  addrProvince: item.addrProvince,
                  addrCity: item.addrCity,
                  addrCounty: item.addrCounty,
                  inpCode: item.inpCode || ''
                })
              })
              this.total = result.total;
            } else {
              // this.$message('当日无预约信息');
            }
          } else {
            this.$message.error(result.msg || "获取病人列表请求失败");
          }
          this.tlm_isLoading = false;
        } catch (error) {
          this.tlm_isLoading = false;
          this.$message.error(error.msg || "获取病人列表请求失败");
        }
      },
      //读卡
      async readCard() {
        if (this.param.cardId == '') {
          this.$message.error("未获得卡号");
        } else {
          this.loading = true;
          try {
            //获取数据接口
            let result = await swipe({cardNum: this.param.cardId});
            if (result.code == '1' && result.data) {
              if (typeof (result.data.patientId) != "undefined" && result.data.patientId != "") {
                this.patientListUnit = [{
                  headImg: require('@/assets/pojehjdz.jpeg'),
                  qrcodeImg: 'data:image/png;base64,',
                  patientId: result.data.patientId,
                  patientName: result.data.patientName,
                  identificationNum: result.data.identificationNum,
                  identificationType: result.data.identificationType,
                  insuranceType: result.data.insuranceType,
                  mobileNum: result.data.mobileNum,
                  chronicStatus: result.data.chronicStatus,
                  cNum: result.data.cardData,
                  createdDate: result.data.archCreateDate,
                  visitCode: result.data.visitCode,
                  patientCode: result.data.patientCode,
                  ouptDeptId: result.data.ouptDeptId,
                  birthDate: result.data.birthDate,
                  archCreateState: result.data.archCreateState || '',
                  nativePlaceCode: result.data.nativePlaceCode || '',
                  ssType: result.data.ssType || '',//社保
                  addrProvince: result.data.addrProvince,
                  addrCity: result.data.addrCity,
                  addrCounty: result.data.addrCounty,
                  inpCode: result.data.inpCode || '',
                  patientAge: result.data.patientAge,
                  patientGender: result.data.patientGender
                }];
                this.patientList = this.patientListUnit;
                await this.changeRecPatientData({
                  ...this.patientList[0],
                  cardId: this.param.cardId,
                  patientId: result.data.patientId,
                  cNum: this.param.cardId
                });
                await this.changePatientListData({
                  patientList: [...this.patientList],
                  pageNo: 1,
                  total: 1,
                  leftTabsValue: {
                    search: this.param.cardId || ''
                  },
                  searchType: this.searchType,
                  clear: this.param.clearData
                });
              } else {
                if (result.data.cardType == '0') {
                  this.$message.error("卡类型错误");
                  //清空vuex
                  // await this.changePatientListData({});
                  // this.changeRecPatientData({});
                } else {
                  if (this.param.clearData == '1') {
                    this.changeRecPatientData({cardId: this.param.cardId, cardType: result.data.cardType});
                    await this.changePatientListData({
                      patientList: [],
                      pageNo: 1,
                      total: 0,
                      leftTabsValue: {
                        search: this.param.cardId || ''
                      },
                      searchType: this.searchType,
                      clear: '1'
                    });
                  } else if (this.param.clearData == '2') {
                    //不清空原数据
                    let patientListData = {...this.patientListData};
                    patientListData.searchType = 'searchButton';
                    patientListData.clear = '2';
                    patientListData.cardInfo = {
                      cardId: this.param.cardId, cardType: result.data.cardType
                    };
                    await this.changePatientListData({...patientListData});
                  }
                  this.$message("该卡未绑定档案，开始建档");
                  this.$router.push('/patientMaster/recordCreate');
                }
              }
              this.total = 1;
            } else {
              this.$message.error(result.msg || '"读卡请求失败"')
            }
            this.loading = false;
          } catch (error) {
            this.loading = false;
            this.$message.error(error.msg || "读卡请求失败");
          }
        }
      },
      //滑动加载更多信息
      load() {
        const _this = this;
        this.pageNo++;
        setTimeout(async () => {
          let patientList = [..._this.patientList];
          if (_this.pageNo > _this.totalPage) {
            // _this.tlm_noMore = true;
            _this.pageNo = _this.totalPage;
          } else {
            //获取分页列表
            let param = {
              ..._this.params,
              pageNo: _this.pageNo,
              pageSize: _this.pageSize
            };
            _this.$delete(param,'date');
            await _this.getPatientList(param);
            _this.patientList = [...patientList.concat(_this.patientListUnit)];
            let leftTabsValue = {
              ..._this.param,
              timeRange: [_this.param["startTimeRange"] || '', _this.param["endTimeRange"] || '']
            };
            // _this.$delete(_this.param,date);
            _this.changePatientListData({
              patientList: [..._this.patientList],
              pageNo: _this.pageNo,
              total: _this.total,
              leftTabsValue: leftTabsValue,
              searchType: _this.searchType,
              clear: this.receivePatientData.patientId ? '1' : '2'
            });
          }
          _this.tlm_isLoading = false;
        }, 2000)
      },
      //点击表格某一列
      rowClick(row) {
        // this.$emit('getPatientInfo', row);
        this.changeRecPatientData(row);
        let leftTabsValue = {
          ...this.param,
          timeRange: [this.param["startTimeRange"] || '', this.param["endTimeRange"] || '']
        };
        let patientListData = {...this.patientListData};
        patientListData.clear = row.patientId ? '1' : '2';
        this.changePatientListData(
          patientListData
          /*{
            patientList: [...this.patientList],
            pageNo: this.pageNo,
            total: this.total,
            leftTabsValue: leftTabsValue,
            searchType: this.searchType,
            clear: row.patientId ? '1' : '2'
          }*/
        )

      },
      tableRowClassName({row, rowIndex}) {
        if (row.archCreateState == '0') {
          //未建档
          return 'archCreateState0'
        } else if (row.archCreateState == '1') {
          //建档未实名
          return 'archCreateState1'
        } else if (row.archCreateState == '2') {
          //建档实名
          return 'archCreateState2'
        } else {
          return ''
        }
      }
    }
  }
</script>

<style lang="scss">
  .leftList {
    padding: 20px;
    padding-top: 0;

    .el-table__body tr.hover-row > td {
      background-color: transparent;
    }

    .el-table__body tr.hover-row.current-row > td {
      background-color: $l-color-bgcolor-12;
    }

    .el-table .archCreateState0 {
      background-color: #E4E4E4;
    }

    .el-table .archCreateState1 {
      background-color: $l-color-white;
    }

    .el-table .archCreateState2 {
      background-color: $l-color-bgcolor-1;
    }
  }
</style>
